// header scrolling
function headerScroll(){
	var pageY = window.pageYOffset || document.documentElement.scrollTop;
	if (pageY == 0){
		$(".header").removeClass("header_scrolled");
	}else{
		$(".header").addClass("header_scrolled");
	}
}
$(document).ready(function() {
	headerScroll();
	$(document).scroll(function(){
		headerScroll();
	});
});
