// Up-down
var updownElem = document.createElement('div');
updownElem.id = "updown";
var child = document.createElement('div');
child.id = "updown-container";
updownElem.appendChild(child);

var pageYLabel = 0;

updownElem.onclick = function() {
    var pageY = window.pageYOffset || document.documentElement.scrollTop;

    switch (this.className) {
        case 'up':
            pageYLabel = pageY;
            window.scrollTo(0, 0);
            this.className = 'down';
            break;

        case 'down':
            window.scrollTo(0, pageYLabel);
            this.className = 'up';
    }
};

setTimeout('document.body.appendChild(updownElem)', 3000);

window.onscroll = function() {
    var pageY = window.pageYOffset || document.documentElement.scrollTop;
    var innerHeight = document.documentElement.clientHeight;

    switch (updownElem.className) {
        case '':
            if (pageY < innerHeight) {
                updownElem.className = 'up';
            }
            break;

        case 'up':
            if (pageY == 0) {
                updownElem.className = '';
            }
            break;

        case 'down':
            if (pageY != 0) {
                updownElem.className = 'up';
                pageYLabel = 0;
            }
            break;

    }
};

function updownResize(){
	updownElem.style.width = String((window.innerWidth - 1200) / 2) + "px";
};
updownResize();
window.onresize = updownResize;
